import React, { useState } from "react";
import { Shield, Lock, Upload, FileText, CheckCircle, LogOut, User, UserPlus, ArrowRight } from "lucide-react";

const API_URL = "http://localhost:5000/api";

function App() {
  const [view, setView] = useState("welcome"); // welcome | login | register | otp | dashboard
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [files, setFiles] = useState([]);

  const sendOtp = async (channel) => {
    if (!phone) return alert("Please enter phone number");
    if (view === "register" && !name) return alert("Please enter your Name");

    try {
      const res = await fetch(`${API_URL}/send-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, channel })
      });
      const data = await res.json();
      if (data.success) {
        setView("otp");
        alert("OTP Sent! Check your phone.");
      } else {
        alert("Error: " + (data.error || "Failed"));
      }
    } catch (e) { alert("Backend connect nahi ho raha."); }
  };

  const verifyOtp = async () => {
    try {
      const res = await fetch(`${API_URL}/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, code: otp })
      });
      const data = await res.json();
      if (data.success) setView("dashboard");
      else alert("Wrong OTP");
    } catch (e) { alert("Verification Failed"); }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles([...files, { name: file.name, size: (file.size/1024).toFixed(1)+" KB" }]);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center p-4 font-sans">
      <h1 className="text-3xl font-bold mb-8 flex gap-2 items-center"><Shield className="text-blue-500"/> SecureVault</h1>

      {/* 1. WELCOME SCREEN */}
      {view === "welcome" && (
        <div className="bg-gray-800 p-8 rounded-xl border border-gray-700 w-full max-w-sm text-center">
          <h2 className="text-xl mb-6">Welcome</h2>
          <div className="space-y-4">
            <button onClick={() => setView("register")} className="w-full bg-blue-600 hover:bg-blue-700 p-3 rounded font-medium flex items-center justify-center gap-2">
              <UserPlus size={20}/> Create New Account
            </button>
            <button onClick={() => setView("login")} className="w-full bg-gray-700 hover:bg-gray-600 p-3 rounded font-medium flex items-center justify-center gap-2">
              <User size={20}/> Login
            </button>
          </div>
        </div>
      )}

      {/* 2. REGISTER SCREEN */}
      {view === "register" && (
        <div className="bg-gray-800 p-8 rounded-xl border border-gray-700 w-full max-w-sm">
          <h2 className="text-xl mb-6 flex gap-2"><UserPlus size={20}/> Create Account</h2>
          <div className="space-y-4">
            <div>
              <label className="block text-sm text-gray-400 mb-1">Full Name</label>
              <input className="w-full p-3 bg-gray-900 border border-gray-600 rounded text-white" placeholder="Enter your name" value={name} onChange={e => setName(e.target.value)} />
            </div>
            <div>
              <label className="block text-sm text-gray-400 mb-1">Phone Number</label>
              <input className="w-full p-3 bg-gray-900 border border-gray-600 rounded text-white" placeholder="+919876543210" value={phone} onChange={e => setPhone(e.target.value)} />
            </div>
            <button onClick={() => sendOtp("sms")} className="w-full bg-blue-600 hover:bg-blue-700 p-3 rounded font-medium">Verify & Register</button>
            <button onClick={() => setView("welcome")} className="w-full text-gray-400 text-sm mt-2">Back</button>
          </div>
        </div>
      )}

      {/* 3. LOGIN SCREEN */}
      {view === "login" && (
        <div className="bg-gray-800 p-8 rounded-xl border border-gray-700 w-full max-w-sm">
          <h2 className="text-xl mb-6 flex gap-2"><User size={20}/> Login</h2>
          <div className="space-y-4">
            <div>
              <label className="block text-sm text-gray-400 mb-1">Phone Number</label>
              <input className="w-full p-3 bg-gray-900 border border-gray-600 rounded text-white" placeholder="+919876543210" value={phone} onChange={e => setPhone(e.target.value)} />
            </div>
            <button onClick={() => sendOtp("sms")} className="w-full bg-green-600 hover:bg-green-700 p-3 rounded font-medium">Get OTP</button>
            <button onClick={() => setView("welcome")} className="w-full text-gray-400 text-sm mt-2">Back</button>
          </div>
        </div>
      )}

      {/* 4. OTP SCREEN */}
      {view === "otp" && (
        <div className="bg-gray-800 p-8 rounded-xl border border-gray-700 w-full max-w-sm">
          <h2 className="text-xl mb-4">Enter OTP</h2>
          <p className="text-sm text-gray-400 mb-4">Code sent to {phone}</p>
          <input className="w-full p-3 mb-4 bg-gray-900 border border-gray-600 rounded text-center text-2xl tracking-widest" placeholder="1234" value={otp} onChange={e => setOtp(e.target.value)} />
          <button onClick={verifyOtp} className="w-full bg-blue-600 hover:bg-blue-700 p-3 rounded font-bold">Submit</button>
        </div>
      )}

      {/* 5. DASHBOARD */}
      {view === "dashboard" && (
        <div className="bg-gray-800 p-8 rounded-xl border border-gray-700 w-full max-w-2xl">
           <div className="flex justify-between items-center mb-6 border-b border-gray-700 pb-4">
             <div>
                <h2 className="text-xl text-green-400 flex gap-2"><CheckCircle /> Encrypted Vault</h2>
                {name && <p className="text-sm text-gray-400 mt-1">Welcome, {name}!</p>}
             </div>
             <button onClick={() => {setView("welcome"); setName(""); setPhone("");}} className="text-gray-400 hover:text-red-400 flex gap-1"><LogOut size={18}/> Logout</button>
           </div>

           <div className="bg-gray-900/50 border-2 border-dashed border-gray-600 p-8 rounded-xl text-center mb-6">
             <Upload className="mx-auto mb-2 text-blue-400" size={32} />
             <p className="text-gray-400">Upload Secure Files</p>
             <input type="file" className="hidden" id="fup" onChange={handleFileUpload} />
             <label htmlFor="fup" className="block mt-4 bg-blue-600 w-32 mx-auto py-2 rounded cursor-pointer">Browse</label>
           </div>

           <div>
             <h3 className="text-gray-400 uppercase text-xs font-bold mb-3">Your Files</h3>
             {files.map((f, i) => (
               <div key={i} className="bg-gray-700/50 p-3 rounded mb-2 flex justify-between items-center">
                 <div className="flex gap-3 items-center"><FileText size={18}/> <span>{f.name}</span></div>
                 <span className="text-xs text-green-400 border border-green-500/30 px-2 py-1 rounded">Encrypted</span>
               </div>
             ))}
           </div>
        </div>
      )}
    </div>
  );
}
export default App;
