import React, { useState } from "react";
import { Lock, Upload, FileText, CheckCircle, LogOut, User, UserPlus, Phone, ArrowRight } from "lucide-react";

const API_URL = "http://localhost:5000/api";

function App() {
  const [view, setView] = useState("login"); // Default view login like image
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [files, setFiles] = useState([]);

  const sendOtp = async (channel) => {
    if (!phone) return alert("Please enter phone number");
    if (view === "register" && !name) return alert("Please enter your Name");

    try {
      const res = await fetch(`${API_URL}/send-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, channel })
      });
      const data = await res.json();
      if (data.success) {
        setView("otp");
        alert("OTP Sent! Check your phone.");
      } else {
        alert("Error: " + (data.error || "Failed"));
      }
    } catch (e) { alert("Backend connect nahi ho raha."); }
  };

  const verifyOtp = async () => {
    try {
      const res = await fetch(`${API_URL}/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, code: otp })
      });
      const data = await res.json();
      if (data.success) setView("dashboard");
      else alert("Wrong OTP");
    } catch (e) { alert("Verification Failed"); }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles([...files, { name: file.name, size: (file.size/1024).toFixed(1)+" KB" }]);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-4 font-sans text-gray-800">

      {/* LOGIN SCREEN (Matches Image) */}
      {view === "login" && (
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md text-center">
          <div className="flex justify-center mb-4">
             <Lock className="w-8 h-8 text-orange-500" />
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-2">Secure Login</h2>
          <p className="text-red-500 text-sm mb-6">Please log in to access this page.</p>

          <div className="space-y-4 text-left">
            <div>
              <input 
                className="w-full p-3 bg-blue-50 border border-blue-100 rounded focus:outline-none focus:ring-2 focus:ring-green-400 text-gray-700" 
                placeholder="Phone Number (+91...)" 
                value={phone} 
                onChange={e => setPhone(e.target.value)} 
              />
            </div>

            <button onClick={() => sendOtp("sms")} className="w-full bg-green-500 hover:bg-green-600 text-white py-3 rounded font-bold transition-colors shadow-md">
              Login (Get OTP)
            </button>

            <div className="text-center mt-4">
              <button onClick={() => setView("register")} className="text-gray-400 text-sm hover:text-gray-600">
                New here? Create an account
              </button>
            </div>
          </div>
        </div>
      )}

      {/* REGISTER SCREEN */}
      {view === "register" && (
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md text-center">
          <div className="flex justify-center mb-4">
             <UserPlus className="w-8 h-8 text-blue-500" />
          </div>
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Create Account</h2>

          <div className="space-y-4 text-left">
            <input 
                className="w-full p-3 bg-blue-50 border border-blue-100 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" 
                placeholder="Full Name" 
                value={name} 
                onChange={e => setName(e.target.value)} 
            />
            <input 
                className="w-full p-3 bg-blue-50 border border-blue-100 rounded focus:outline-none focus:ring-2 focus:ring-blue-400" 
                placeholder="Phone Number (+91...)" 
                value={phone} 
                onChange={e => setPhone(e.target.value)} 
            />

            <button onClick={() => sendOtp("sms")} className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3 rounded font-bold transition-colors shadow-md">
              Verify & Register
            </button>

            <div className="text-center mt-4">
              <button onClick={() => setView("login")} className="text-gray-400 text-sm hover:text-gray-600">
                Already have an account? Login
              </button>
            </div>
          </div>
        </div>
      )}

      {/* OTP SCREEN */}
      {view === "otp" && (
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-md text-center">
          <h2 className="text-xl font-bold mb-4">Enter OTP</h2>
          <p className="text-sm text-gray-500 mb-6">Code sent to {phone}</p>
          <input 
            className="w-full p-3 bg-gray-50 border border-gray-200 rounded text-center text-2xl tracking-widest mb-6 focus:outline-none focus:ring-2 focus:ring-green-500" 
            placeholder="123456" 
            value={otp} 
            onChange={e => setOtp(e.target.value)} 
          />
          <button onClick={verifyOtp} className="w-full bg-green-500 hover:bg-green-600 text-white py-3 rounded font-bold shadow-md">
            Verify Login
          </button>
        </div>
      )}

      {/* DASHBOARD (White Theme) */}
      {view === "dashboard" && (
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-2xl border border-gray-100">
           <div className="flex justify-between items-center mb-6 border-b border-gray-100 pb-4">
             <div>
                <h2 className="text-xl font-bold text-gray-800 flex items-center gap-2">
                  <CheckCircle className="text-green-500" /> SecureVault
                </h2>
                {name && <p className="text-sm text-gray-500 mt-1">Welcome, {name}!</p>}
             </div>
             <button onClick={() => {setView("login"); setName(""); setPhone("");}} className="text-gray-400 hover:text-red-500 flex items-center gap-1 text-sm font-medium">
               <LogOut size={18}/> Logout
             </button>
           </div>

           <div className="bg-blue-50 border-2 border-dashed border-blue-200 p-8 rounded-xl text-center mb-6 hover:bg-blue-100 transition-colors">
             <Upload className="mx-auto mb-2 text-blue-500" size={32} />
             <p className="text-gray-600 font-medium">Upload Secure Files</p>
             <p className="text-xs text-gray-400 mt-1">Max 5MB ï¿½ Encrypted</p>
             <input type="file" className="hidden" id="fup" onChange={handleFileUpload} />
             <label htmlFor="fup" className="block mt-4 bg-blue-600 hover:bg-blue-700 text-white w-40 mx-auto py-2 rounded cursor-pointer font-medium shadow-sm">Browse Files</label>
           </div>

           <div>
             <h3 className="text-gray-400 uppercase text-xs font-bold mb-3 tracking-wider">Your Encrypted Files</h3>
             {files.length === 0 ? (
               <div className="text-center text-gray-400 text-sm py-4">No files uploaded yet.</div>
             ) : (
               files.map((f, i) => (
                 <div key={i} className="bg-gray-50 p-3 rounded mb-2 flex justify-between items-center border border-gray-100">
                   <div className="flex gap-3 items-center text-gray-700">
                     <FileText size={18} className="text-gray-400"/> 
                     <span className="font-medium">{f.name}</span>
                   </div>
                   <span className="text-xs text-green-600 bg-green-100 px-2 py-1 rounded font-medium">AES-256 Encrypted</span>
                 </div>
               ))
             )}
           </div>
        </div>
      )}
    </div>
  );
}
export default App;
