import React, { useState, useEffect } from "react";
import { Lock, Upload, FileText, CheckCircle, LogOut, User, UserPlus, Key } from "lucide-react";

const API_URL = "http://localhost:5000/api";

function App() {
  const [view, setView] = useState("login");

  const [name, setName] = useState("Deepchand Kumawat");
  const [phone, setPhone] = useState("8905974138");
  const [password, setPassword] = useState("");
  const [otp, setOtp] = useState("");
  const [files, setFiles] = useState([]);

  // --- STYLES (Green Theme matching your photo) ---
  const styles = {
    container: { minHeight: "100vh", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", backgroundColor: "#f0fdf4", fontFamily: "Arial, sans-serif" },
    card: { backgroundColor: "white", padding: "40px", borderRadius: "12px", boxShadow: "0 4px 20px rgba(0,0,0,0.1)", width: "100%", maxWidth: "400px", textAlign: "center", border: "1px solid #bbf7d0" },
    header: { color: "#166534", marginBottom: "10px", fontSize: "24px", fontWeight: "bold" },
    subHeader: { color: "#ef4444", fontSize: "14px", marginBottom: "30px" },
    input: { width: "100%", padding: "12px", marginBottom: "15px", border: "1px solid #86efac", borderRadius: "6px", backgroundColor: "#f0fdf4", fontSize: "16px", outline: "none", boxSizing: "border-box", color: "#333" },
    button: { width: "100%", padding: "12px", backgroundColor: "#22c55e", color: "white", border: "none", borderRadius: "6px", fontSize: "16px", fontWeight: "bold", cursor: "pointer", transition: "background 0.3s" },
    link: { color: "#16a34a", fontSize: "14px", background: "none", border: "none", marginTop: "15px", cursor: "pointer", textDecoration: "underline" },
    iconBox: { backgroundColor: "#dcfce7", padding: "15px", borderRadius: "50%", display: "inline-block", marginBottom: "15px" },
    fileItem: { display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px", borderBottom: "1px solid #eee", backgroundColor: "#fff" },
    uploadBox: { border: "2px dashed #4ade80", padding: "30px", borderRadius: "10px", backgroundColor: "#f0fdf4", cursor: "pointer", marginBottom: "20px" }
  };

  // 1. REGISTER NEW USER
  const handleRegister = () => {
    if (!name || !phone || !password) return alert("Please fill all fields");

    // Save to LocalStorage (Acting as Database)
    const user = { name, phone, password };
    localStorage.setItem("secureUser", JSON.stringify(user));

    alert("Registration Successful! Please Login.");
    setView("login");
    // Clear fields except name for convenience, or clear all
    setPassword(""); 
  };

  // 2. LOGIN WITH PASSWORD
  const handleLogin = () => {
    // Get saved user from "Database"
    const savedUser = JSON.parse(localStorage.getItem("secureUser"));

    // If no user found, maybe use default hardcoded for demo? 
    // Or strictly check. Let's strictly check for "Registered" feel.
    if (!savedUser) {
        // Fallback for demo if they haven't registered yet but want to try default
        if (name === "Deepchand Kumawat" && password === "admin") {
             sendOtp("sms", "8905974138");
             return;
        }
        return alert("User not found. Please Register first.");
    }

    if (name === savedUser.name && password === savedUser.password) {
        // Password Matched! Now Send OTP to registered phone
        setPhone(savedUser.phone);
        sendOtp("sms", savedUser.phone);
    } else {
        alert("Invalid Username or Password!");
    }
  };

  const sendOtp = async (channel, targetPhone) => {
    try {
      const res = await fetch(`${API_URL}/send-otp`, {
        method: "POST", headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: targetPhone || phone, channel })
      });
      const data = await res.json();
      if (data.success) { setView("otp"); alert(`Password Verified! OTP Sent to ${targetPhone || phone}`); } 
      else { alert("Error: " + (data.error || "Failed")); }
    } catch (e) { alert("Backend connect nahi ho raha."); }
  };

  const verifyOtp = async () => {
    try {
      const res = await fetch(`${API_URL}/verify-otp`, {
        method: "POST", headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, code: otp })
      });
      const data = await res.json();
      if (data.success) setView("dashboard");
      else alert("Wrong OTP");
    } catch (e) { alert("Verification Failed"); }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles([...files, { name: file.name }]);
  };

  return (
    <div style={styles.container}>

      {/* LOGIN SCREEN */}
      {view === "login" && (
        <div style={styles.card}>
          <div style={styles.iconBox}><Lock size={32} color="#16a34a" /></div>
          <h2 style={styles.header}>Secure Login</h2>
          <p style={styles.subHeader}>Enter Password to Access</p>

          <input 
            style={styles.input} 
            placeholder="Username" 
            value={name} 
            onChange={e => setName(e.target.value)} 
          />
          <input 
            style={styles.input} 
            type="password"
            placeholder="Enter Password" 
            value={password} 
            onChange={e => setPassword(e.target.value)} 
          />

          <button style={styles.button} onClick={handleLogin}>Login</button>
          <button style={styles.link} onClick={() => setView("register")}>New here? Create an account</button>
        </div>
      )}

      {/* REGISTER SCREEN */}
      {view === "register" && (
        <div style={styles.card}>
          <div style={styles.iconBox}><UserPlus size={32} color="#16a34a" /></div>
          <h2 style={styles.header}>Create Account</h2>

          <input style={styles.input} placeholder="Full Name" value={name} onChange={e => setName(e.target.value)} />
          <input style={styles.input} placeholder="Phone Number (+91...)" value={phone} onChange={e => setPhone(e.target.value)} />
          <input style={styles.input} type="password" placeholder="Set Password" value={password} onChange={e => setPassword(e.target.value)} />

          <button style={styles.button} onClick={handleRegister}>Register</button>
          <button style={styles.link} onClick={() => setView("login")}>Already have an account? Login</button>
        </div>
      )}

      {/* OTP SCREEN */}
      {view === "otp" && (
        <div style={styles.card}>
          <h2 style={styles.header}>Enter OTP</h2>
          <p style={{color:"#666", marginBottom:"20px"}}>Password Verified. OTP sent to {phone}</p>
          <input style={{...styles.input, textAlign:"center", letterSpacing:"5px", fontSize:"24px"}} placeholder="����" value={otp} onChange={e => setOtp(e.target.value)} />
          <button style={styles.button} onClick={verifyOtp}>Verify & Enter Vault</button>
        </div>
      )}

      {/* DASHBOARD */}
      {view === "dashboard" && (
        <div style={{...styles.card, maxWidth:"600px"}}>
           <div style={{display:"flex", justifyContent:"space-between", alignItems:"center", marginBottom:"20px", borderBottom:"1px solid #eee", paddingBottom:"10px"}}>
             <h2 style={{...styles.header, margin:0, display:"flex", alignItems:"center", gap:"10px"}}>
               <CheckCircle color="#22c55e"/> SecureVault
             </h2>
             <button style={{background:"none", border:"none", color:"red", cursor:"pointer", display:"flex", alignItems:"center", gap:"5px"}} onClick={() => setView("login")}>
               <LogOut size={16}/> Logout
             </button>
           </div>

           <div style={styles.uploadBox}>
             <Upload size={40} color="#22c55e" style={{margin:"0 auto", display:"block", marginBottom:"10px"}}/>
             <p style={{color:"#166534", fontWeight:"bold"}}>Upload Secure Files</p>
             <p style={{fontSize:"12px", color:"#888"}}>Max 5MB � AES-256 Encrypted</p>
             <input type="file" id="fup" style={{display:"none"}} onChange={handleFileUpload} />
             <label htmlFor="fup" style={{...styles.button, display:"inline-block", width:"auto", padding:"10px 30px", marginTop:"15px"}}>Browse Files</label>
           </div>

           <div style={{textAlign:"left"}}>
             <h3 style={{fontSize:"12px", color:"#888", textTransform:"uppercase", fontWeight:"bold", marginBottom:"10px"}}>Your Encrypted Files</h3>
             {files.length === 0 ? <p style={{textAlign:"center", color:"#ccc"}}>No files yet.</p> : files.map((f, i) => (
               <div key={i} style={styles.fileItem}>
                 <div style={{display:"flex", alignItems:"center", gap:"10px"}}>
                   <FileText size={18} color="#666"/> <b>{f.name}</b>
                 </div>
                 <span style={{background:"#dcfce7", color:"#166534", padding:"2px 8px", borderRadius:"4px", fontSize:"10px"}}>Encrypted</span>
               </div>
             ))}
           </div>
        </div>
      )}
    </div>
  );
}
export default App;
