import React, { useState } from "react";
import { Lock, Upload, FileText, CheckCircle, LogOut, User, UserPlus, Phone, ArrowRight } from "lucide-react";

const API_URL = "http://localhost:5000/api";

function App() {
  const [view, setView] = useState("login"); // Default view login
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [files, setFiles] = useState([]);

  const sendOtp = async (channel) => {
    if (!phone) return alert("Please enter phone number");

    // Check name only if registering, or if you want to force name on login too (optional)
    if (view === "register" && !name) return alert("Please enter your Name");

    try {
      const res = await fetch(`${API_URL}/send-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, channel })
      });
      const data = await res.json();
      if (data.success) {
        setView("otp");
        alert("OTP Sent! Check your phone.");
      } else {
        alert("Error: " + (data.error || "Failed"));
      }
    } catch (e) { alert("Backend connect nahi ho raha."); }
  };

  const verifyOtp = async () => {
    try {
      const res = await fetch(`${API_URL}/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, code: otp })
      });
      const data = await res.json();
      if (data.success) setView("dashboard");
      else alert("Wrong OTP");
    } catch (e) { alert("Verification Failed"); }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles([...files, { name: file.name, size: (file.size/1024).toFixed(1)+" KB" }]);
  };

  return (
    <div className="min-h-screen bg-green-50 flex flex-col items-center justify-center p-4 font-sans text-gray-800">

      {/* LOGIN SCREEN (Green Theme) */}
      {view === "login" && (
        <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md text-center border border-green-100">
          <div className="flex justify-center mb-4">
             <div className="bg-green-100 p-3 rounded-full">
               <Lock className="w-8 h-8 text-green-600" />
             </div>
          </div>
          <h2 className="text-2xl font-bold text-gray-700 mb-2">Secure Login</h2>
          <p className="text-gray-500 text-sm mb-8">Please log in to access this page.</p>

          <div className="space-y-6 text-left">
            {/* Added Username Input */}
            <input 
                className="w-full p-4 bg-green-50 border border-green-200 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 text-gray-700 font-medium placeholder-gray-400 transition-all" 
                placeholder="Username" 
                value={name} 
                onChange={e => setName(e.target.value)} 
            />

            <input 
                className="w-full p-4 bg-green-50 border border-green-200 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 text-gray-700 font-medium placeholder-gray-400 transition-all" 
                placeholder="Phone Number (+91...)" 
                value={phone} 
                onChange={e => setPhone(e.target.value)} 
            />

            <button onClick={() => sendOtp("sms")} className="w-full bg-green-600 hover:bg-green-700 text-white py-4 rounded-md font-bold transition-all shadow-md text-lg hover:shadow-lg">
              Login
            </button>

            <div className="text-center mt-4">
              <button onClick={() => setView("register")} className="text-green-600 text-sm hover:text-green-800 transition-colors font-medium">
                New here? Create an account
              </button>
            </div>
          </div>
        </div>
      )}

      {/* REGISTER SCREEN (Green Theme) */}
      {view === "register" && (
        <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md text-center border border-green-100">
          <div className="flex justify-center mb-4">
             <div className="bg-green-100 p-3 rounded-full">
               <UserPlus className="w-8 h-8 text-green-600" />
             </div>
          </div>
          <h2 className="text-2xl font-bold text-gray-700 mb-8">Create Account</h2>

          <div className="space-y-6 text-left">
            <input 
                className="w-full p-4 bg-green-50 border border-green-200 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 text-gray-700 font-medium placeholder-gray-400 transition-all" 
                placeholder="Full Name" 
                value={name} 
                onChange={e => setName(e.target.value)} 
            />
            <input 
                className="w-full p-4 bg-green-50 border border-green-200 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 text-gray-700 font-medium placeholder-gray-400 transition-all" 
                placeholder="Phone Number (+91...)" 
                value={phone} 
                onChange={e => setPhone(e.target.value)} 
            />

            <button onClick={() => sendOtp("sms")} className="w-full bg-green-600 hover:bg-green-700 text-white py-4 rounded-md font-bold transition-all shadow-md text-lg hover:shadow-lg">
              Verify & Register
            </button>

            <div className="text-center mt-4">
              <button onClick={() => setView("login")} className="text-green-600 text-sm hover:text-green-800 transition-colors font-medium">
                Already have an account? Login
              </button>
            </div>
          </div>
        </div>
      )}

      {/* OTP SCREEN (Green Theme) */}
      {view === "otp" && (
        <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md text-center border border-green-100">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Enter OTP</h2>
          <p className="text-sm text-gray-500 mb-8">Code sent to {phone}</p>
          <input 
            className="w-full p-4 bg-green-50 border border-green-200 rounded-md text-center text-3xl tracking-[0.5em] mb-8 focus:outline-none focus:ring-2 focus:ring-green-500 font-bold text-gray-700 transition-all" 
            placeholder="������" 
            maxLength={6}
            value={otp} 
            onChange={e => setOtp(e.target.value)} 
          />
          <button onClick={verifyOtp} className="w-full bg-green-600 hover:bg-green-700 text-white py-4 rounded-md font-bold shadow-md text-lg transition-all hover:shadow-lg">
            Verify Login
          </button>
        </div>
      )}

      {/* DASHBOARD (Green Theme) */}
      {view === "dashboard" && (
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-3xl border border-green-100">
           <div className="flex justify-between items-center mb-8 border-b border-green-100 pb-6">
             <div>
                <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-3">
                  <CheckCircle className="text-green-600 w-6 h-6" /> SecureVault
                </h2>
                {name && <p className="text-sm text-green-600 mt-1 font-medium">Welcome back, {name}!</p>}
             </div>
             <button onClick={() => {setView("login"); setName(""); setPhone("");}} className="text-gray-400 hover:text-red-500 flex items-center gap-2 text-sm font-medium transition-colors">
               <LogOut size={18}/> Logout
             </button>
           </div>

           <div className="bg-green-50 border-2 border-dashed border-green-200 p-10 rounded-xl text-center mb-8 hover:bg-green-100 transition-colors group cursor-pointer">
             <div className="bg-white p-4 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4 shadow-sm group-hover:scale-110 transition-transform">
                <Upload className="text-green-600 w-8 h-8" />
             </div>
             <p className="text-gray-700 font-semibold text-lg">Upload Secure Files</p>
             <p className="text-xs text-gray-400 mt-2">Max 5MB � AES-256 Encrypted</p>
             <input type="file" className="hidden" id="fup" onChange={handleFileUpload} />
             <label htmlFor="fup" className="block mt-6 bg-green-600 hover:bg-green-700 text-white w-48 mx-auto py-3 rounded-md cursor-pointer font-bold shadow-sm transition-colors">Browse Files</label>
           </div>

           <div>
             <h3 className="text-gray-400 uppercase text-xs font-bold mb-4 tracking-widest ml-1">Your Encrypted Files</h3>
             {files.length === 0 ? (
               <div className="text-center text-gray-400 text-sm py-8 bg-green-50 rounded-lg border border-green-100">No files uploaded yet.</div>
             ) : (
               <div className="space-y-3">
                 {files.map((f, i) => (
                   <div key={i} className="bg-white p-4 rounded-lg flex justify-between items-center border border-green-100 shadow-sm hover:shadow-md transition-shadow">
                     <div className="flex gap-4 items-center text-gray-700">
                       <div className="bg-green-50 p-2 rounded">
                         <FileText size={20} className="text-green-600"/> 
                       </div>
                       <span className="font-semibold">{f.name}</span>
                     </div>
                     <span className="text-xs text-green-700 bg-green-100 px-3 py-1.5 rounded-full font-bold flex items-center gap-1">
                       <CheckCircle size={12} /> Encrypted
                     </span>
                   </div>
                 ))}
               </div>
             )}
           </div>
        </div>
      )}
    </div>
  );
}
export default App;
