import React, { useState } from "react";
import { Lock, Upload, FileText, CheckCircle, LogOut, User, UserPlus, Phone, ArrowRight } from "lucide-react";

const API_URL = "http://localhost:5000/api";

function App() {
  const [view, setView] = useState("login"); // Default view login to match image
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [files, setFiles] = useState([]);

  const sendOtp = async (channel) => {
    if (!phone) return alert("Please enter phone number");
    if (view === "register" && !name) return alert("Please enter your Name");

    try {
      const res = await fetch(`${API_URL}/send-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, channel })
      });
      const data = await res.json();
      if (data.success) {
        setView("otp");
        alert("OTP Sent! Check your phone.");
      } else {
        alert("Error: " + (data.error || "Failed"));
      }
    } catch (e) { alert("Backend connect nahi ho raha."); }
  };

  const verifyOtp = async () => {
    try {
      const res = await fetch(`${API_URL}/verify-otp`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ phoneNumber: phone, code: otp })
      });
      const data = await res.json();
      if (data.success) setView("dashboard");
      else alert("Wrong OTP");
    } catch (e) { alert("Verification Failed"); }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles([...files, { name: file.name, size: (file.size/1024).toFixed(1)+" KB" }]);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col items-center justify-center p-4 font-sans text-gray-800">

      {/* LOGIN SCREEN (Matches Image Style) */}
      {view === "login" && (
        <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md text-center border border-gray-100">
          <div className="flex justify-center mb-4">
             <Lock className="w-8 h-8 text-orange-400" />
          </div>
          <h2 className="text-2xl font-bold text-gray-700 mb-2">Secure Login</h2>
          <p className="text-red-500 text-sm mb-8">Please log in to access this page.</p>

          <div className="space-y-6 text-left">
            <div>
              <input 
                className="w-full p-4 bg-blue-50 border border-blue-100 rounded-md focus:outline-none focus:ring-2 focus:ring-green-400 text-gray-700 font-medium placeholder-gray-400" 
                placeholder="Phone Number (+91...)" 
                value={phone} 
                onChange={e => setPhone(e.target.value)} 
              />
            </div>

            <button onClick={() => sendOtp("sms")} className="w-full bg-green-500 hover:bg-green-600 text-white py-4 rounded-md font-bold transition-colors shadow-sm text-lg">
              Login
            </button>

            <div className="text-center mt-4">
              <button onClick={() => setView("register")} className="text-gray-400 text-sm hover:text-gray-600 transition-colors">
                New here? Create an account
              </button>
            </div>
          </div>
        </div>
      )}

      {/* REGISTER SCREEN */}
      {view === "register" && (
        <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md text-center border border-gray-100">
          <div className="flex justify-center mb-4">
             <UserPlus className="w-8 h-8 text-blue-500" />
          </div>
          <h2 className="text-2xl font-bold text-gray-700 mb-8">Create Account</h2>

          <div className="space-y-6 text-left">
            <input 
                className="w-full p-4 bg-blue-50 border border-blue-100 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-700 font-medium placeholder-gray-400" 
                placeholder="Full Name" 
                value={name} 
                onChange={e => setName(e.target.value)} 
            />
            <input 
                className="w-full p-4 bg-blue-50 border border-blue-100 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 text-gray-700 font-medium placeholder-gray-400" 
                placeholder="Phone Number (+91...)" 
                value={phone} 
                onChange={e => setPhone(e.target.value)} 
            />

            <button onClick={() => sendOtp("sms")} className="w-full bg-blue-600 hover:bg-blue-700 text-white py-4 rounded-md font-bold transition-colors shadow-sm text-lg">
              Verify & Register
            </button>

            <div className="text-center mt-4">
              <button onClick={() => setView("login")} className="text-gray-400 text-sm hover:text-gray-600 transition-colors">
                Already have an account? Login
              </button>
            </div>
          </div>
        </div>
      )}

      {/* OTP SCREEN */}
      {view === "otp" && (
        <div className="bg-white p-10 rounded-lg shadow-xl w-full max-w-md text-center border border-gray-100">
          <h2 className="text-2xl font-bold text-gray-800 mb-4">Enter OTP</h2>
          <p className="text-sm text-gray-500 mb-8">Code sent to {phone}</p>
          <input 
            className="w-full p-4 bg-gray-50 border border-gray-200 rounded-md text-center text-3xl tracking-[0.5em] mb-8 focus:outline-none focus:ring-2 focus:ring-green-500 font-bold text-gray-700" 
            placeholder="������" 
            maxLength={6}
            value={otp} 
            onChange={e => setOtp(e.target.value)} 
          />
          <button onClick={verifyOtp} className="w-full bg-green-500 hover:bg-green-600 text-white py-4 rounded-md font-bold shadow-sm text-lg transition-colors">
            Verify Login
          </button>
        </div>
      )}

      {/* DASHBOARD (Matches Light Theme) */}
      {view === "dashboard" && (
        <div className="bg-white p-8 rounded-lg shadow-xl w-full max-w-3xl border border-gray-100">
           <div className="flex justify-between items-center mb-8 border-b border-gray-100 pb-6">
             <div>
                <h2 className="text-2xl font-bold text-gray-800 flex items-center gap-3">
                  <CheckCircle className="text-green-500 w-6 h-6" /> SecureVault
                </h2>
                {name && <p className="text-sm text-gray-500 mt-1 font-medium">Welcome back, {name}!</p>}
             </div>
             <button onClick={() => {setView("login"); setName(""); setPhone("");}} className="text-gray-400 hover:text-red-500 flex items-center gap-2 text-sm font-medium transition-colors">
               <LogOut size={18}/> Logout
             </button>
           </div>

           <div className="bg-blue-50 border-2 border-dashed border-blue-200 p-10 rounded-xl text-center mb-8 hover:bg-blue-100 transition-colors group cursor-pointer">
             <div className="bg-white p-4 rounded-full w-16 h-16 flex items-center justify-center mx-auto mb-4 shadow-sm group-hover:scale-110 transition-transform">
                <Upload className="text-blue-500 w-8 h-8" />
             </div>
             <p className="text-gray-700 font-semibold text-lg">Upload Secure Files</p>
             <p className="text-xs text-gray-400 mt-2">Max 5MB � AES-256 Encrypted</p>
             <input type="file" className="hidden" id="fup" onChange={handleFileUpload} />
             <label htmlFor="fup" className="block mt-6 bg-blue-600 hover:bg-blue-700 text-white w-48 mx-auto py-3 rounded-md cursor-pointer font-bold shadow-sm transition-colors">Browse Files</label>
           </div>

           <div>
             <h3 className="text-gray-400 uppercase text-xs font-bold mb-4 tracking-widest ml-1">Your Encrypted Files</h3>
             {files.length === 0 ? (
               <div className="text-center text-gray-400 text-sm py-8 bg-gray-50 rounded-lg border border-gray-100">No files uploaded yet.</div>
             ) : (
               <div className="space-y-3">
                 {files.map((f, i) => (
                   <div key={i} className="bg-white p-4 rounded-lg flex justify-between items-center border border-gray-100 shadow-sm hover:shadow-md transition-shadow">
                     <div className="flex gap-4 items-center text-gray-700">
                       <div className="bg-gray-100 p-2 rounded">
                         <FileText size={20} className="text-gray-500"/> 
                       </div>
                       <span className="font-semibold">{f.name}</span>
                     </div>
                     <span className="text-xs text-green-700 bg-green-100 px-3 py-1.5 rounded-full font-bold flex items-center gap-1">
                       <CheckCircle size={12} /> Encrypted
                     </span>
                   </div>
                 ))}
               </div>
             )}
           </div>
        </div>
      )}
    </div>
  );
}
export default App;
