import React, { useState, useEffect } from "react";
import { Lock, Upload, FileText, CheckCircle, LogOut, User, Shield, Activity, Clock, Share2, Settings, Moon, Sun, X, Trash2, RefreshCcw, Key, Download, AlertTriangle } from "lucide-react";
import CryptoJS from "crypto-js"; // Asli Encryption ke liye

const API_URL = "http://localhost:5000/api";
const SECRET_KEY = "my-super-secret-key-123"; // Is key se sab encrypt hoga

function App() {
  const [view, setView] = useState("login");
  
  // --- USER & APP STATE ---
  const [currentUser, setCurrentUser] = useState(() => JSON.parse(localStorage.getItem("secureCurrentUser")));
  
  // Form States
  const [name, setName] = useState("Deepchand Kumawat");
  const [email, setEmail] = useState("deepchandkhowal123@gmail.com");
  const [password, setPassword] = useState("");
  const [otp, setOtp] = useState("");

  // UI States
  const [darkMode, setDarkMode] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  // --- DATA STORAGE (Users, Files, Logs) ---
  const [users, setUsers] = useState(() => {
      const saved = localStorage.getItem("secureUsersList");
      return saved ? JSON.parse(saved) : [{ 
        id: 1, name: "Deepchand Kumawat", email: "deepchandkhowal123@gmail.com", password: "admin", role: "Admin" 
      }];
  });

  const [files, setFiles] = useState(() => JSON.parse(localStorage.getItem("secureFiles") || "[]"));
  const [trash, setTrash] = useState(() => JSON.parse(localStorage.getItem("secureTrash") || "[]"));
  const [logs, setLogs] = useState(() => JSON.parse(localStorage.getItem("secureLogs") || "[]"));

  // --- DATA PERSISTENCE ---
  useEffect(() => {
    localStorage.setItem("secureFiles", JSON.stringify(files));
    localStorage.setItem("secureTrash", JSON.stringify(trash));
    localStorage.setItem("secureUsersList", JSON.stringify(users));
    localStorage.setItem("secureLogs", JSON.stringify(logs));
    localStorage.setItem("secureCurrentUser", JSON.stringify(currentUser));
  }, [files, trash, users, logs, currentUser]);

  useEffect(() => { if(currentUser) setView("dashboard"); }, [currentUser]);

  // --- THEME CONFIG ---
  const theme = {
    bg: darkMode ? "#0f172a" : "#f8fafc",
    text: darkMode ? "#f1f5f9" : "#334155",
    cardBg: darkMode ? "#1e293b" : "white",
    navbar: darkMode ? "#020617" : "#2c3e50",
    border: darkMode ? "#334155" : "#e2e8f0",
    accent: "#3b82f6"
  };

  // --- LOGGING SYSTEM (Audit Trails) ---
  const addLog = (action, details) => {
    const newLog = { 
        id: Date.now(), 
        user: currentUser ? currentUser.name : "System", 
        action, 
        details, 
        time: new Date().toLocaleString() 
    };
    setLogs([newLog, ...logs]);
  };

  // --- ENCRYPTION HELPERS ---
  const encryptData = (text) => CryptoJS.AES.encrypt(text, SECRET_KEY).toString();
  const decryptData = (cipher) => {
      try {
          const bytes = CryptoJS.AES.decrypt(cipher, SECRET_KEY);
          return bytes.toString(CryptoJS.enc.Utf8);
      } catch (e) { return null; }
  };

  // --- AUTHENTICATION ---
  const sendOtpApi = async (targetEmail) => {
    try {
      const res = await fetch(`${API_URL}/send-otp`, {
        method: "POST", headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: targetEmail })
      });
      const data = await res.json();
      return data.success;
    } catch (e) { alert("Backend Error"); return false; }
  };

  const handleLogin = async () => {
    const foundUser = users.find(u => u.email === email && u.password === password);
    if (foundUser) {
        const sent = await sendOtpApi(foundUser.email);
        if(sent) { 
            localStorage.setItem("tempUser", JSON.stringify(foundUser));
            setView("otp"); 
            alert(`OTP Sent to ${foundUser.email}`); 
        }
    } else { alert("Invalid Credentials"); addLog("Login Failed", `Attempt for ${email}`); }
  };

  const verifyLoginOtp = async () => {
    try {
      const res = await fetch(`${API_URL}/verify-otp`, {
        method: "POST", headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, code: otp })
      });
      const data = await res.json();
      if (data.success) {
          const tempUser = JSON.parse(localStorage.getItem("tempUser"));
          setCurrentUser(tempUser);
          setView("dashboard");
          addLog("Login Success", `${tempUser.role} Access Granted`);
      } else alert("Wrong OTP");
    } catch (e) { alert("Verification Failed"); }
  };

  const handleRegister = () => {
     if(!name || !email || !password) return alert("Fill all fields");
     if(users.find(u => u.email === email)) return alert("User exists");
     
     // First user or specific email is Admin
     const role = (users.length === 0 || email === "deepchandkhowal123@gmail.com") ? "Admin" : "User";
     const newUser = { id: Date.now(), name, email, password, role };
     
     setUsers([...users, newUser]);
     addLog("New User", `${name} registered as ${role}`);
     alert("Registered! Please Login.");
     setView("login");
  };

  // --- FILE OPERATIONS ---
  const processFile = (file) => {
      const reader = new FileReader();
      
      // Fake Progress Bar
      let progress = 0;
      const interval = setInterval(() => {
          progress += 10;
          setUploadProgress(progress);
          if(progress >= 100) clearInterval(interval);
      }, 150);

      reader.onload = function(event) {
          setTimeout(() => {
            const rawData = event.target.result;
            const encryptedData = encryptData(rawData); // REAL ENCRYPTION
            
            // File Versioning Logic (v1, v2)
            let finalName = file.name;
            let version = 1;
            while(files.find(f => f.name === finalName && f.owner === currentUser.email)) {
                const nameParts = file.name.split(".");
                const ext = nameParts.pop();
                const base = nameParts.join(".");
                finalName = `${base}_v${version}.${ext}`;
                version++;
            }

            const newFile = {
                id: Date.now(),
                name: finalName,
                data: encryptedData, // Store Encrypted
                size: (file.size/1024).toFixed(1) + " KB",
                type: file.type,
                owner: currentUser.email,
                date: new Date().toLocaleDateString()
            };

            setFiles([newFile, ...files]);
            setUploadProgress(0);
            addLog("File Upload", `${finalName} encrypted and saved.`);
            alert("File Encrypted (AES-256) & Uploaded!");
          }, 2000);
      };
      reader.readAsDataURL(file);
  };

  const handleFileUpload = (e) => { if(e.target.files[0]) processFile(e.target.files[0]); };
  const handleDrag = (e) => { e.preventDefault(); e.stopPropagation(); if(e.type.includes("drag")) setDragActive(true); else setDragActive(false); };
  const handleDrop = (e) => { e.preventDefault(); e.stopPropagation(); setDragActive(false); if(e.dataTransfer.files[0]) processFile(e.dataTransfer.files[0]); };

  const handleDownload = (file) => {
      if(file.isDemo) return alert("Demo file.");
      const decryptedData = decryptData(file.data); // REAL DECRYPTION
      if(!decryptedData) return alert("Decryption Failed! Key mismatch.");
      
      const a = document.createElement("a");
      a.href = decryptedData;
      a.download = file.name;
      document.body.appendChild(a); a.click(); document.body.removeChild(a);
      addLog("File Download", `${file.name} decrypted by ${currentUser.name}`);
  };

  const handleShare = (fileId) => {
      // Share Logic with Expiry (Simulation)
      const link = `https://securevault.app/share/${fileId}?exp=${Date.now() + 3600000}`;
      navigator.clipboard.writeText(link);
      alert(`Secure Link Copied!\n(Valid for 1 Hour)\n\n${link}`);
      addLog("File Share", `Generated link for File ID ${fileId}`);
  };

  // --- ADMIN ACTIONS ---
  const deleteUser = (id) => {
      if(window.confirm("Delete User? All their files will be lost.")) {
          setUsers(users.filter(u => u.id !== id));
          setFiles(files.filter(f => f.owner !== users.find(u=>u.id===id).email));
          addLog("User Deleted", `Admin deleted user ID ${id}`);
      }
  };

  // --- STYLES ---
  const styles = {
      container: { minHeight: "100vh", backgroundColor: theme.bg, color: theme.text, fontFamily: "Segoe UI, sans-serif", transition: "0.3s" },
      navbar: { padding: "15px 30px", backgroundColor: theme.navbar, color: "white", display: "flex", justifyContent: "space-between", alignItems: "center", boxShadow: "0 2px 10px rgba(0,0,0,0.2)" },
      card: { backgroundColor: theme.cardBg, padding: "25px", borderRadius: "10px", boxShadow: "0 4px 6px rgba(0,0,0,0.05)", marginBottom: "20px", border: `1px solid ${theme.border}` },
      btn: { padding: "10px 15px", borderRadius: "6px", border: "none", cursor: "pointer", fontWeight: "bold", display: "flex", alignItems: "center", gap: "8px", color: "white" },
      input: { width: "100%", padding: "12px", margin: "8px 0", borderRadius: "6px", border: "1px solid #ccc", backgroundColor: darkMode?"#334155":"#fff", color: theme.text }
  };

  // --- RENDER ---
  if(!currentUser && !["login","register","otp"].includes(view)) { setView("login"); return null; }

  return (
    <div style={styles.container}>
       
       {/* AUTH SCREENS */}
       {!currentUser ? (
           <div style={{display:"flex", height:"100vh", justifyContent:"center", alignItems:"center"}}>
               <div style={{...styles.card, width:"350px", textAlign:"center"}}>
                   <div style={{marginBottom:"20px"}}><Lock size={40} color="#22c55e" style={{margin:"auto"}}/></div>
                   <h2 style={{marginBottom:"20px"}}>SecureVault Pro</h2>
                   {view === "login" && (
                       <>
                        <input style={styles.input} placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)}/>
                        <input style={styles.input} type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)}/>
                        <button style={{...styles.btn, width:"100%", justifyContent:"center", backgroundColor:"#22c55e"}} onClick={handleLogin}>Login</button>
                        <div style={{marginTop:"15px", fontSize:"14px", color:"#3b82f6", cursor:"pointer"}} onClick={()=>setView("register")}>Create Account</div>
                       </>
                   )}
                   {view === "register" && (
                       <>
                        <input style={styles.input} placeholder="Full Name" value={name} onChange={e=>setName(e.target.value)}/>
                        <input style={styles.input} placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)}/>
                        <input style={styles.input} type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)}/>
                        <button style={{...styles.btn, width:"100%", justifyContent:"center", backgroundColor:"#22c55e"}} onClick={handleRegister}>Register</button>
                        <div style={{marginTop:"15px", fontSize:"14px", color:"#3b82f6", cursor:"pointer"}} onClick={()=>setView("login")}>Back to Login</div>
                       </>
                   )}
                   {view === "otp" && (
                       <>
                        <p>Enter OTP sent to {email}</p>
                        <input style={{...styles.input, textAlign:"center", letterSpacing:"5px"}} placeholder="0000" value={otp} onChange={e=>setOtp(e.target.value)}/>
                        <button style={{...styles.btn, width:"100%", justifyContent:"center", backgroundColor:"#22c55e"}} onClick={verifyLoginOtp}>Verify</button>
                       </>
                   )}
               </div>
           </div>
       ) : (
           /* DASHBOARD */
           <>
             <nav style={styles.navbar}>
                 <div style={{display:"flex", alignItems:"center", gap:"10px", fontSize:"20px", fontWeight:"bold"}}>
                     <Shield fill="#f1c40f" /> SecureVault <span style={{fontSize:"10px", background:"#22c55e", padding:"2px 6px", borderRadius:"4px"}}>PRO</span>
                 </div>
                 <div style={{display:"flex", gap:"15px", alignItems:"center"}}>
                     {currentUser.role === "Admin" && (
                         <button style={{...styles.btn, backgroundColor:"#f59e0b"}} onClick={()=>setView("logs")}><Activity size={16}/> Logs</button>
                     )}
                     <button onClick={()=>setDarkMode(!darkMode)} style={{background:"none", border:"none", color:"white", cursor:"pointer"}}>{darkMode?<Sun/>:<Moon/>}</button>
                     <div style={{textAlign:"right", fontSize:"12px"}}><div>{currentUser.name}</div><div style={{opacity:0.7}}>{currentUser.role}</div></div>
                     <button style={{...styles.btn, backgroundColor:"#ef4444"}} onClick={handleLogout}><LogOut size={16}/></button>
                 </div>
             </nav>

             <div style={{maxWidth:"1000px", margin:"30px auto", padding:"0 20px"}}>
                 {/* LOGS VIEW (ADMIN ONLY) */}
                 {view === "logs" && currentUser.role === "Admin" ? (
                     <div style={styles.card}>
                         <div style={{display:"flex", justifyContent:"space-between", marginBottom:"20px"}}>
                             <h3>Security Audit Logs</h3>
                             <button onClick={()=>setView("dashboard")} style={{...styles.btn, backgroundColor:"gray"}}>Back</button>
                         </div>
                         <div style={{maxHeight:"400px", overflowY:"auto", fontSize:"14px"}}>
                             {logs.map(log => (
                                 <div key={log.id} style={{padding:"10px", borderBottom:`1px solid ${theme.border}`, display:"flex", justifyContent:"space-between"}}>
                                     <span><b>{log.user}</b>: {log.action} ({log.details})</span>
                                     <span style={{color:"gray", fontSize:"12px"}}>{log.time}</span>
                                 </div>
                             ))}
                         </div>
                     </div>
                 ) : (
                     /* MAIN DASHBOARD */
                     <>
                        <div style={styles.card}>
                            <h3><Upload size={20} style={{display:"inline"}}/> Upload Secure File</h3>
                            {uploadProgress > 0 && <div style={{height:"5px", background:"#eee", margin:"10px 0"}}><div style={{width:`${uploadProgress}%`, height:"100%", background:"#22c55e", transition:"width 0.2s"}}></div></div>}
                            <div 
                                style={{border:"2px dashed #3b82f6", borderRadius:"8px", padding:"40px", textAlign:"center", marginTop:"10px", cursor:"pointer", backgroundColor: dragActive ? "#dcfce7" : theme.uploadBg}}
                                onDragEnter={(e)=>{e.preventDefault(); setDragActive(true)}} 
                                onDragLeave={(e)=>{e.preventDefault(); setDragActive(false)}}
                                onDragOver={(e)=>e.preventDefault()}
                                onDrop={(e)=>{e.preventDefault(); setDragActive(false); if(e.dataTransfer.files[0]) processFile(e.dataTransfer.files[0]);}}
                            >
                                <input type="file" id="fup" style={{display:"none"}} onChange={handleFileUpload}/>
                                <label htmlFor="fup" style={{cursor:"pointer"}}>
                                    <Upload size={40} color="#3b82f6" style={{margin:"auto"}}/>
                                    <p style={{fontWeight:"bold", marginTop:"10px"}}>Drag & Drop or Click to Upload</p>
                                    <p style={{fontSize:"12px", color:"gray"}}>AES-256 End-to-End Encryption</p>
                                </label>
                            </div>
                        </div>

                        <div style={styles.card}>
                            <h3><Folder size={20} style={{display:"inline"}}/> {currentUser.role==="Admin" ? "All Encrypted Files" : "My Files"}</h3>
                            {files.filter(f => currentUser.role==="Admin" || f.owner === currentUser.email || f.isDemo).map(f => (
                                <div key={f.id} style={{display:"flex", justifyContent:"space-between", alignItems:"center", padding:"15px", borderBottom:`1px solid ${theme.border}`}}>
                                    <div style={{display:"flex", gap:"10px", alignItems:"center"}}>
                                        <FileText color="#3b82f6"/>
                                        <div>
                                            <div style={{fontWeight:"bold"}}>{f.name}</div>
                                            <div style={{fontSize:"12px", color:"gray"}}>{f.size} â€¢ {f.date} {currentUser.role==="Admin" && `â€¢ Owner: ${f.owner}`}</div>
                                        </div>
                                    </div>
                                    <div style={{display:"flex", gap:"10px"}}>
                                        <button onClick={()=>handleShare(f.id)} style={{...styles.btn, backgroundColor:"#8b5cf6", padding:"8px"}}><Share2 size={16}/></button>
                                        <button onClick={()=>handleDownload(f)} style={{...styles.btn, backgroundColor:"#22c55e", padding:"8px"}}><Download size={16}/></button>
                                        <button onClick={()=>moveToTrash(f.id)} style={{...styles.btn, backgroundColor:"#ef4444", padding:"8px"}}><Trash2 size={16}/></button>
                                    </div>
                                </div>
                            ))}
                            {files.length === 0 && <p style={{textAlign:"center", color:"gray"}}>No files.</p>}
                        </div>
                     </>
                 )}
             </div>
           </>
       )}
    </div>
  );
}
export default App;
